// 或者
for (let <span class="token punctuation">[</span>key<span class="token punctuation">,</span> value<span class="token punctuation">]</span> of map.entries(<span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
